import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Dashboard() {
  const [user, setUser] = useState(null);
  const [showReportForm, setShowReportForm] = useState(false);
  const [reportFormData, setReportFormData] = useState({
    name: '',
    description: '',
    userLocation: '',
    type: 'infrastructure'
  });
  const [formError, setFormError] = useState('');
  const [formSuccess, setFormSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const [userReports, setUserReports] = useState([]);
  const [reportsFetched, setReportsFetched] = useState(false);
  const navigate = useNavigate();

  // Function to fetch user reports
  const fetchUserReports = async () => {
    try {
      const token = localStorage.getItem('token');

      if (!token) {
        console.error('No token found, cannot fetch reports');
        return;
      }

      const response = await axios.get(
        'http://localhost:5000/api/reports/user/my-reports',
        {
          headers: {
            'x-auth-token': token
          }
        }
      );

      if (response.data.success) {
        console.log('User reports:', response.data.data);
        setUserReports(response.data.data);
      } else {
        console.error('Failed to fetch reports:', response.data.message);
      }
    } catch (err) {
      console.error('Error fetching user reports:', err);
    } finally {
      setReportsFetched(true);
    }
  };

  useEffect(() => {
    // Check if user is logged in
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');

    if (!token) {
      navigate('/login');
      return;
    }

    if (userData) {
      setUser(JSON.parse(userData));
      // Fetch user's reports when component mounts
      fetchUserReports();
    }
  }, [navigate]);

  const handleReportChange = (e) => {
    setReportFormData({
      ...reportFormData,
      [e.target.name]: e.target.value
    });
  };

  const handleReportSubmit = async (e) => {
    e.preventDefault();
    setFormError('');
    setFormSuccess('');
    setLoading(true);

    try {
      // Get the auth token
      const token = localStorage.getItem('token');

      if (!token) {
        setFormError('Authentication error. Please login again.');
        return;
      }

      // Create proper GeoJSON Point
      const locationData = {
        type: 'Point',
        coordinates: [-79.3832, 43.6532] // Toronto coordinates [longitude, latitude]
      };

      // Create the report payload with all required fields
      const reportData = {
        name: reportFormData.name,
        description: reportFormData.description,
        userLocation: reportFormData.userLocation,
        type: reportFormData.type,
        location: locationData
      };

      console.log('Submitting report with data:', JSON.stringify(reportData, null, 2));

      // Make the API request with auth token
      const response = await axios.post(
        'http://localhost:5000/api/reports',
        reportData,
        {
          headers: {
            'x-auth-token': token,
            'Content-Type': 'application/json'
          }
        }
      );

      console.log('Response:', response.data);

      if (response.data.success) {
        setFormSuccess('Report submitted successfully!');
        setReportFormData({
          name: '',
          description: '',
          userLocation: '',
          type: 'infrastructure'
        });
        setShowReportForm(false);

        // Refresh reports list after successful submission
        fetchUserReports();
      } else {
        setFormError(response.data.message || 'Unknown error occurred');
      }
    } catch (err) {
      console.error('Error submitting report:', err);

      // Log all error details to help debug
      if (err.response) {
        console.error('Error status:', err.response.status);
        console.error('Error data:', err.response.data);
        console.error('Error headers:', err.response.headers);

        setFormError(err.response.data?.message || `Server error: ${err.response.status}`);
      } else if (err.request) {
        console.error('No response received:', err.request);
        setFormError('No response from server. Please try again later.');
      } else {
        console.error('Error message:', err.message);
        setFormError(`Error: ${err.message}`);
      }
    } finally {
      setLoading(false);
    }
  };

  // Format date for display
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  // Helper function to render status with appropriate CSS class
  const renderStatus = (status) => {
    let statusClass = '';

    switch(status) {
      case 'Waiting for verification':
        statusClass = 'status-waiting';
        break;
      case 'Verified':
        statusClass = 'status-verified';
        break;
      case 'In progress':
        statusClass = 'status-in-progress';
        break;
      case 'Solved':
        statusClass = 'status-solved';
        break;
      default:
        statusClass = '';
    }

    return (
      <span className={`status-indicator ${statusClass}`}>
        {status}
      </span>
    );
  };

  // If not logged in, the useEffect will redirect
  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="dashboard">
      <h1>Welcome, {user.username}!</h1>

      {formSuccess && (
        <div className="alert-success">
          {formSuccess}
        </div>
      )}

      <div className="dashboard-card">
        <h2>Your Profile</h2>
        <p><strong>Username:</strong> {user.username}</p>
        <p><strong>Email:</strong> {user.email}</p>
      </div>

      <div className="dashboard-card">
        <h2>Report a Problem</h2>
        {!showReportForm ? (
          <>
            <p>Help improve your city by reporting issues you encounter.</p>
            <button 
              className="btn btn-primary" 
              onClick={() => setShowReportForm(true)}
            >
              Create New Report
            </button>
          </>
        ) : (
          <form onSubmit={handleReportSubmit}>
            {formError && (
              <div className="alert-error">{formError}</div>
            )}

            <div className="form-group">
              <label htmlFor="name">Report Title</label>
              <input
                type="text"
                id="name"
                name="name"
                value={reportFormData.name}
                onChange={handleReportChange}
                className="form-control"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="type">Type of Issue</label>
              <select
                id="type"
                name="type"
                value={reportFormData.type}
                onChange={handleReportChange}
                className="form-control"
                required
              >
                <option value="infrastructure">Infrastructure</option>
                <option value="cleanliness">Cleanliness</option>
                <option value="human">Human-related</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="description">Description</label>
              <textarea
                id="description"
                name="description"
                value={reportFormData.description}
                onChange={handleReportChange}
                className="form-control"
                rows="5"
                required
              ></textarea>
            </div>

            <div className="form-group">
              <label htmlFor="userLocation">Your Location</label>
              <input
                type="text"
                id="userLocation"
                name="userLocation"
                value={reportFormData.userLocation}
                onChange={handleReportChange}
                className="form-control"
                placeholder="e.g., 123 Main St, Toronto"
                required
              />
            </div>

            <div className="form-actions">
              <button 
                type="button" 
                className="btn btn-secondary"
                onClick={() => setShowReportForm(false)}
              >
                Cancel
              </button>
              <button 
                type="submit" 
                className="btn btn-primary"
                disabled={loading}
              >
                {loading ? 'Submitting...' : 'Submit Report'}
              </button>
            </div>
          </form>
        )}
      </div>

      <div className="dashboard-card">
        <h2>Your Reports</h2>
        {!reportsFetched ? (
          <p>Loading your reports...</p>
        ) : userReports.length === 0 ? (
          <p>You haven't submitted any reports yet.</p>
        ) : (
          <div className="reports-list">
            {userReports.map(report => (
              <div key={report._id} className="report-item">
                <h3>{report.name}</h3>
                <div className="report-details">
                  <p><strong>Type:</strong> {report.type}</p>
                  <p><strong>Status:</strong> {renderStatus(report.status)}</p>
                  <p><strong>Description:</strong> {report.description}</p>
                  <p><strong>Location:</strong> {report.userLocation}</p>
                  <p><strong>Submitted:</strong> {formatDate(report.createdAt)}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
