import React, { useState, useEffect, useCallback, useRef } from 'react';
import { reportService } from '../services/api';
import { GoogleMap, useJsApiLoader, Marker, InfoWindow } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '700px'
};

// Toronto as default center
const defaultCenter = {
  lat: 43.6532,
  lng: -79.3832
};

const mapOptions = {
  disableDefaultUI: false,
  zoomControl: true,
  streetViewControl: true,
  mapTypeControl: true,
};

const libraries = ['places'];

function Map() {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedReport, setSelectedReport] = useState(null);
  const [searchValue, setSearchValue] = useState('');
  const [mapCenter, setMapCenter] = useState(defaultCenter);
  const mapRef = useRef(null);
  const searchBoxRef = useRef(null);

  // Load Google Maps API
  const { isLoaded, loadError } = useJsApiLoader({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY || '',
    libraries
  });

  // Format date for display
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  // Initialize search box
  const initSearchBox = useCallback(() => {
    if (isLoaded && window.google && searchBoxRef.current) {
      const searchBox = new window.google.maps.places.SearchBox(searchBoxRef.current);

      searchBox.addListener('places_changed', () => {
        const places = searchBox.getPlaces();

        if (places.length === 0) return;

        const place = places[0];
        if (!place.geometry || !place.geometry.location) return;

        const newCenter = {
          lat: place.geometry.location.lat(),
          lng: place.geometry.location.lng()
        };

        setMapCenter(newCenter);

        if (mapRef.current) {
          mapRef.current.panTo(newCenter);
          mapRef.current.setZoom(15);
        }
      });
    }
  }, [isLoaded]);

  // Fetch all reports from the API
  const fetchReports = useCallback(async () => {
    try {
      setLoading(true);
      const response = await reportService.getAll();

      if (response.data.success) {
        console.log('Fetched reports:', response.data.data);
        setReports(response.data.data);
      } else {
        setError('Failed to load reports');
      }
    } catch (err) {
      console.error('Error fetching reports:', err);
      setError('Error loading reports. Please try again later.');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  useEffect(() => {
    if (isLoaded) {
      initSearchBox();
    }
  }, [isLoaded, initSearchBox]);

  const onMapLoad = useCallback((map) => {
    mapRef.current = map;
    initSearchBox(); // Initialize search box after map loads
  }, [initSearchBox]);

  const handleSearchChange = (e) => {
    setSearchValue(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    // The searchBox will handle the place search through the places_changed event
  };

  const getMarkerIcon = (type) => {
    switch (type) {
      case 'infrastructure':
        return { url: 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png' };
      case 'cleanliness':
        return { url: 'http://maps.google.com/mapfiles/ms/icons/green-dot.png' };
      case 'human':
        return { url: 'http://maps.google.com/mapfiles/ms/icons/orange-dot.png' };
      default:
        return { url: 'http://maps.google.com/mapfiles/ms/icons/red-dot.png' };
    }
  };

  const filterReports = (type) => {
    fetchReports().then(() => {
      if (type && type !== 'all') {
        setReports(prevReports => prevReports.filter(report => report.type === type));
      }
    });
  };

  // Render loading state
  if (loadError) {
    return <div>Error loading Google Maps</div>;
  }

  if (!isLoaded) {
    return <div>Loading Maps...</div>;
  }

  return (
    <div className="map-page">
      <h1>City Reports Map</h1>

      <div className="map-controls">
        <form onSubmit={handleSearchSubmit} className="search-form">
          <input
            ref={searchBoxRef}
            type="text"
            placeholder="Search for a location"
            value={searchValue}
            onChange={handleSearchChange}
            className="search-input"
          />
          <button type="submit" className="btn btn-primary">Search</button>
        </form>

        <div className="filter-controls">
          <label>Filter by type:</label>
          <select onChange={(e) => filterReports(e.target.value)} className="filter-select">
            <option value="all">All Reports</option>
            <option value="infrastructure">Infrastructure</option>
            <option value="cleanliness">Cleanliness</option>
            <option value="human">Human-related</option>
          </select>
          <button 
            onClick={() => fetchReports()} 
            className="btn btn-secondary"
          >
            Reset Filters
          </button>
        </div>
      </div>

      <div className="map-legend">
        <div className="legend-item">
          <div className="legend-marker" style={{ backgroundColor: '#4285F4' }}></div>
          <span>Infrastructure</span>
        </div>
        <div className="legend-item">
          <div className="legend-marker" style={{ backgroundColor: '#34A853' }}></div>
          <span>Cleanliness</span>
        </div>
        <div className="legend-item">
          <div className="legend-marker" style={{ backgroundColor: '#FBBC05' }}></div>
          <span>Human-related</span>
        </div>
      </div>

      {error && (
        <div className="alert-error">{error}</div>
      )}

      {loading ? (
        <div className="loading-container">Loading reports...</div>
      ) : (
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={mapCenter}
          zoom={12}
          onLoad={onMapLoad}
          options={mapOptions}
        >
          {reports.map(report => {
            // Ensure report has valid location data
            if (report.location && 
                report.location.coordinates && 
                Array.isArray(report.location.coordinates) && 
                report.location.coordinates.length === 2) {

              const position = {
                lat: report.location.coordinates[1], // latitude is second in GeoJSON format
                lng: report.location.coordinates[0], // longitude is first in GeoJSON format
              };

              return (
                <Marker
                  key={report._id}
                  position={position}
                  icon={getMarkerIcon(report.type)}
                  onClick={() => setSelectedReport(report)}
                />
              );
            }
            return null;
          })}

          {selectedReport && (
            <InfoWindow
              position={{
                lat: selectedReport.location.coordinates[1],
                lng: selectedReport.location.coordinates[0]
              }}
              onCloseClick={() => setSelectedReport(null)}
            >
              <div className="info-window">
                <h3>{selectedReport.name}</h3>
                <p><strong>Type:</strong> {selectedReport.type}</p>
                <p><strong>Status:</strong> {selectedReport.status}</p>
                <p><strong>Description:</strong> {selectedReport.description}</p>
                <p><strong>Location:</strong> {selectedReport.userLocation}</p>
                <p><strong>Reported on:</strong> {formatDate(selectedReport.createdAt)}</p>
                {selectedReport.user && selectedReport.user.username && (
                  <p><strong>Reported by:</strong> {selectedReport.user.username}</p>
                )}
              </div>
            </InfoWindow>
          )}
        </GoogleMap>
      )}

      <div className="report-stats">
        <h3>Report Statistics</h3>
        <div className="stats-container">
          <div className="stat-item">
            <span className="stat-label">Total Reports:</span>
            <span className="stat-value">{reports.length}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Infrastructure Issues:</span>
            <span className="stat-value">{reports.filter(r => r.type === 'infrastructure').length}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Cleanliness Issues:</span>
            <span className="stat-value">{reports.filter(r => r.type === 'cleanliness').length}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Human-related Issues:</span>
            <span className="stat-value">{reports.filter(r => r.type === 'human').length}</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Map;
