import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login({ setIsLoggedIn }) {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const { email, password } = formData;

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', formData);

      if (response.data.success) {
        console.log('Login response:', response.data);

        if (!response.data.token) {
          throw new Error('No token received from server');
        }

        // Store the token and user data in localStorage
        localStorage.setItem('token', response.data.token);

        // Create a basic user object if one doesn't exist in the response
        let userData = {};

        if (response.data.user) {
          // User data exists in response
          userData = {
            id: response.data.user.id || response.data.user._id,
            username: response.data.user.username || 'User',
            email: response.data.user.email || formData.email
          };
        } else {
          // Create minimal user data from the email
          userData = {
            id: 'unknown',
            username: formData.email.split('@')[0],
            email: formData.email
          };
        }

        console.log('Storing user data:', userData);
        localStorage.setItem('user', JSON.stringify(userData));

        // Update login state in the app
        setIsLoggedIn(true);

        // Redirect to dashboard
        navigate('/dashboard');
      } else {
        setError('Login failed: ' + (response.data.message || 'Unknown error'));
      }

    } catch (err) {
      console.error('Login error:', err);
      let errorMessage = 'Something went wrong';

      if (err.response) {
        errorMessage = err.response.data?.message || `Server error: ${err.response.status}`;
      } else if (err.message) {
        errorMessage = err.message;
      }

      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="form-container">
      <h2 className="form-title">Login</h2>

      {error && (
        <div style={{ color: 'red', marginBottom: '1rem', textAlign: 'center' }}>
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={email}
            onChange={handleChange}
            className="form-control"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={handleChange}
            className="form-control"
            required
          />
        </div>

        <button 
          type="submit" 
          className="btn btn-primary" 
          style={{ width: '100%', marginTop: '1rem' }}
          disabled={loading}
        >
          {loading ? 'Loading...' : 'Login'}
        </button>
      </form>

      <div style={{ marginTop: '1.5rem', textAlign: 'center' }}>
        <p>Don't have an account? <Link to="/register">Register</Link></p>
      </div>
    </div>
  );
}

export default Login;
